<script type="text/javascript">
function readyFn( jQuery ) {
  var sub = document.getElementById('mk-header-subscribe-button');
  sub.setAttribute("aria-expanded", "false");
  console.log("Worked");
  var subs = document.getElementById('mce-EMAIL');
  subs.setAttribute("autocomplete", "");
  var content_li = document.getElementsByClassName('acsb-skip-link');
  jQuery("a.menu-item-link").removeAttr("aria-expanded");
  jQuery("a.menu-item-link").removeAttr("aria-haspopup");
  try{
    setTimeout(function() {
      if(content_li.length != 0){
        console.log(content_li);
        content_li[1].setAttribute("href", "#content");
      }
    }, (3000));
  }catch{
    console.log("Its another Page");
}
  
  //FB 
  var fb = document.getElementsByClassName("facebook-hover ");
  var fb_svg = fb[0].getElementsByClassName('mk-svg-icon')[0];
  fb_svg.setAttribute("role", "img");
  fb_svg.setAttribute("aria-lablledby", "fb");
  var fb_title = document.createElement("title");
  fb_title.innerHTML = "Zankou Facebook";
  fb_title.setAttribute('id', 'fb');
  fb_svg.appendChild(fb_title);
  
  //Twitter twitter-hover 
  var twitter = document.getElementsByClassName("twitter-hover ");
  var tw_svg = twitter[0].getElementsByClassName('mk-svg-icon')[0];
  tw_svg.setAttribute("role", "img");
  tw_svg.setAttribute("aria-lablledby", "twitter");
  var tw_title = document.createElement("title");
  tw_title.innerHTML = "Zankou Twitter";
  tw_title.setAttribute('id', 'twitter');
  tw_svg.appendChild(tw_title);
  
  //Instagram
  var inst = document.getElementsByClassName("instagram-hover ");
  var inst_svg = inst[0].getElementsByClassName('mk-svg-icon')[0];
  inst_svg.setAttribute("role", "img");
  inst_svg.setAttribute("aria-lablledby", "instagram");
  var inst_title = document.createElement("title");
  inst_title.innerHTML = "Zankou Instagram";
  inst_title.setAttribute('id', 'Instagram');
  inst_svg.appendChild(inst_title);
  
  //linkedin-hover 
  var linke = document.getElementsByClassName("linkedin-hover ");
  var linke_svg = linke[0].getElementsByClassName('mk-svg-icon')[0];
  linke_svg.setAttribute("role", "img");
  linke_svg.setAttribute("aria-lablledby", "linkedin");
  var linkd_title = document.createElement("title");
  linkd_title.innerHTML = "Zankou Linkedin";
  linkd_title.setAttribute('id', 'linkedin');
  linke_svg.appendChild(linkd_title);
  
  
  var l = document.getElementsByClassName('footerbutton');
  for(var i=0; i<l.length; i++){
    l[i].setAttribute("style", "color: white !important;");
  }
  
//  var email_title = document.getElementById('mce-EMAIL1');
//  setTimeout(function() {
//    email_title.setAttribute("title", "Enter your email address");
//  }, (4000));
  
  var faq_bg = window.location.pathname;
  if(faq_bg == '/faqs/'){
    setTimeout(function() {
      console.log("FAQ page loaded ");
      var lID = document.getElementsByClassName('ls-wrapper ls-bg-outer');
      var img = lID[0].getElementsByClassName('ls-bg');
      img[0].removeAttribute("alt");
      var g_fo = document.getElementsByClassName('gfield_required gfield_required_asterisk');
      for(var i=0;i<g_fo.length; i++){
        st = g_fo[i].getElementsByTagName('strong');
        if(st.length != 0){
          st[0].innerHTML = "* Mandatory Field";
        }
      }
      console.log("FAQ END");
    }, (3000));
  }
  
  if(faq_bg == '/contact/'){
    setTimeout(function() {
      console.log("Contact Page");
      var g_fo = document.getElementsByClassName('gfield_required gfield_required_asterisk');
      for(var i=0;i<g_fo.length; i++){
        st = g_fo[i].getElementsByTagName('strong');
        st[0].innerHTML = "* Mandatory Field";
      }
      console.log("FAQ END");
    }, (3000));
  }


  if(faq_bg == '/menu/'){
    jQuery("div.widgettitle").replaceWith('<h2 class="widgettitle"><strong>Options</strong></h2>');
    jQuery("h1.page-title").replaceWith('<h1 class="page-title" style="background-color: #00000017;">Menu</h2>');
