  if(faq_bg == '/anaheim/'){
    setTimeout(function() {
      jQuery("h1.page-title").replaceWith('<p class="page-title " style="background-color: #00000017;">Locations</p>');
      console.log("Delivery Images triggered");
    }, (3000));
  }
}
 
