    jQuery("body").on("keydown",".menu-item-has-children:not(.dropdownOpen)", function(e){
        if(e.keyCode == 13) {
          
            var idVal = jQuery(this).closest(".menu-item-has-children").attr("id");
            jQuery(this).find('.sub-menu').find('li').find('a').addClass(idVal);
            jQuery(this).addClass("dropdownOpen");
            jQuery(this).find('a').attr("aria-expanded",'true');
            jQuery(".sub-menu").find(".child-menu a").removeAttr("aria-expanded");
            jQuery(".sub-sub-child").find('a').removeAttr("aria-expanded");
            e.preventDefault();
            //menuFunction();
        }
    });


   
  
    jQuery(".menu-item-has-children a").attr("aria-expanded","false");
    jQuery(".js-smooth-scroll").on("keydown", function(e) {
        if(e.keyCode == 27) {
            if (jQuery(this).parent().parent().prev().prev().length) {
                jQuery(this).parent().parent().prev().prev().focus();
                jQuery(this).parent().parent().prev().prev().parent().removeClass("dropdownOpen");
                jQuery(this).find('a').parent().parent().prev().prev().parent().attr("aria-expanded","false");
                jQuery(".sub-menu").find(".child-menu a").removeAttr("aria-expanded");
                jQuery(".sub-sub-child").find('a').removeAttr("aria-expanded");
            } else {
                jQuery(this).parent().parent().prev().focus();  
                jQuery('li').find('a').attr('aria-expanded',"false");
                jQuery('li').removeClass("dropdownOpen");   
                jQuery(".sub-menu").find(".child-menu a").removeAttr("aria-expanded");
                jQuery(".sub-sub-child").find('a').removeAttr("aria-expanded");
            }
            return
        }
   });
